Item.method_code:
Item responses = this.newItem();
Item requests = this.getRelationships();
int count = requests.getItemCount();
for (int i = 0; i < count; ++i)
{
    Item response = requests.getItemByIndex(i).apply();
    if (!response.isError())
    {
        responses.appendItem(response);
    }
}

return responses;
