Item.sqlserver_body:
create procedure update_activity2_dates(
  @id char(32),
  @date_es varchar(32),
  @date_ef varchar(32),
  @date_ls varchar(32),
  @date_lf varchar(32),
  @date_start_sched varchar(32),
  @date_due_sched varchar(32))
as
begin
 update activity2 set
  date_es = @date_es,
date_ef = @date_ef,
date_ls = @date_ls,
date_lf = @date_lf,
date_start_sched = @date_start_sched,
date_due_sched = @date_due_sched
  where id = @id
select 1
end
