Item.method_code:
if (window.onSaveCommand) {
	aras.browserHelper.toggleSpinner(window.document, true);
	setTimeout(function() {
		window.onSaveCommand().then(function(res) {
			if (!res || (res && !techDocContainer.isEditorLoaded)) {
				aras.browserHelper.toggleSpinner(window.document, false);
			}
		});
	}, 0);
}
