Item.method_code:
if (inArgs.contextParams) {
	var reinitData = {};
	var sender = inArgs.contextParams.sender;
	var isDocumentEdit = this.isEditMode;
	var selectedItems = sender.GetSelectedItems();
	reinitData['cui_disabled'] = !(isDocumentEdit && selectedItems.length > 0);
	return reinitData;
}
